CREATE DEFINER=`root`@`localhost` PROCEDURE `obtenerOfertaEmpleo`(
 varIdOfertaEmpleo int,
 out varFechaVigencia datetime,
 out varFechaPublicacion datetime,
 out varNombreOfertante varchar(100),
 out varEmpresa varchar(100),
 out varUbicacion varchar(100),
 out varNombreUbicacion varchar(100),
 out varCargo varchar(100),
 out varTitulo varchar(100),
 out varPerfilAspirante varchar(100),
 out varDescripcion text,
 out varSalario double,
 out varTiempoExperiencia int,
 out varTieneDocumento tinyint(1),
 out varNivelFormacion int,
 out varNombreNivelFormacion varchar(200),
 out varExperienciaMinina int
)
BEGIN

SELECT 
of.of_fecha_vigencia,
of.of_fecha_publicacion,
of.of_nombre_ofertante,
of.of_empresa,
of.of_ubicacion,
CONCAT(dep.dpto_nombre, ' - ', mun.mpio_nombre),
of.of_cargo,
of.of_titulo,
of.of_perfil_aspirante,
of.of_descripcion,
of.of_salario,
of.of_tiempo_experiencia,
of.of_nivel_formacion,
nf.nf_nombre,
of.of_experiencia_minina
INTO
 varFechaVigencia,
 varFechaPublicacion,
 varNombreOfertante,
 varEmpresa,
 varUbicacion,
 varNombreUbicacion,
 varCargo,
 varTitulo,
 varPerfilAspirante,
 varDescripcion,
 varSalario,
 varTiempoExperiencia,
 varNivelFormacion,
 varNombreNivelFormacion,
 varExperienciaMinina 
FROM con_ofertas_empleo of
 left join buho.ma_municipio mun on mun.cod_mpio = of.of_ubicacion
 left join buho.ma_departamento dep on dep.dpto_cod = mun.mpio_dpto
 left join buho.ma_nivel_formacion nf on nf.nf_id = of.of_nivel_formacion
WHERE of_id = varIdOfertaEmpleo;

SET varTieneDocumento = false;
IF exists(SELECT cod_oferta 
		  FROM con_documento_ofertas_empleo 
          WHERE cod_oferta = varIdOfertaEmpleo) THEN
    SET varTieneDocumento = true;      
END IF;

END ;;
