SELECT 
    em.email
INTO
	varCorreoElectronico
FROM us_usuario usu
  inner join buho.pe_persona pe on usu.us_persona = pe.pe_id
  inner join buho.pe_email em on em.email_persona = pe.pe_id
WHERE usu.us_nombre_usuario = varNombreUsuario LIMIT 1;


END ;;
